package introduction;

public class charvar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char char1='A';
		char char2=65;
		
	  System.out.println("the value of char1:"+char1);
	  System.out.println("the value of char2:"+char2);
	  
	  double d=123.5;
	  int x=(int)d; //to extract the integer part of d
	  System.out.println("value of d is:"+d);
	  System.out.println("value of integer part of d is:"+x);
	  System.out.println("value of integer part of d is:"+(int)d);
	}

}
