package introduction;

public class switchcase {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

		for(int i=0;i<=2;i++)
		{
			switch(i)
			{
			case 0:
			System.out.println("i is 0");
			break;
			
			case 1:
			System.out.println("i is 1");
			break;
			case 2:
				System.out.println("i is 2");
			default:
				System.out.println("i is >2");
		}
	}

}
}
